parameters.jsCode:
/**
 * Robust parser for Groq titles+outlines
 * - Guarantees exactly `want` items
 * - Each item has exactly 5 outlines (auto pad)
 * - Dedupe with existing topics (soft similarity) + unique slug
 */

// ---------- helpers ----------
function normalize(s){ return (s||"").toLowerCase().replace(/[\W_]+/g," ").trim(); }
function slugify(s){ return (s||"").toLowerCase().replace(/[\W_]+/g,"-").replace(/^-+|-+$/g,""); }

function jaccardLike(a,b){
  if(!a||!b) return 0;
  const A=new Set(normalize(a).split(" ").filter(Boolean));
  const B=new Set(normalize(b).split(" ").filter(Boolean));
  if(A.size===0 || B.size===0) return 0;
  let inter=0; A.forEach(x=>{ if(B.has(x)) inter++; });
  const union = new Set([...A, ...B]).size || 1;
  return inter/union;
}
function softSim(a,b){
  const j = jaccardLike(a,b);
  const lr = Math.min((a||"").length,(b||"").length) / Math.max((a||"").length,(b||"").length || 1);
  return 0.65*j + 0.35*lr;  // 0~1
}

function padTo5(title, arr){
  const base = (arr||[]).map(x=>String(x||"").trim()).filter(Boolean).slice(0,5);
  const need = 5 - base.length;
  if(need<=0) return base.slice(0,5);
  // ç®€å•è§„åˆ™è¡¥é½ï¼ˆå¯æŒ‰éœ€æ›¿æ¢æˆæ›´ä¸ªæ€§åŒ–çš„æ¨¡æ¿ï¼‰
  const t = title || "This topic";
  const fillers = [
    `Key considerations for ${t.toLowerCase()}`,
    `Practical steps to get started with ${t.toLowerCase()}`,
    `Common mistakes to avoid in ${t.toLowerCase()}`,
    `Real-world examples and takeaways for ${t.toLowerCase()}`,
    `FAQ: what readers often ask about ${t.toLowerCase()}`
  ];
  for(let i=0;i<need;i++) base.push(fillers[i % fillers.length]);
  return base.slice(0,5);
}

/** ä»æ–‡æœ¬ä¸­æå–ç¬¬ä¸€æ®µ JSON æ•°ç»„ */
function extractJSONArray(text){
  if(!text) return null;
  let s = String(text).trim();

  // å»æ‰ ```json ... ``` æˆ– ``` åŒ…è£¹
  s = s.replace(/^```(?:json|javascript)?/i, "").replace(/```$/,"").trim();

  // ç›´æ¥å°è¯• JSON.parse
  try { const v = JSON.parse(s); if(Array.isArray(v)) return v; } catch(_){}

  // å®½æ¾ï¼šå®šä½é¦–ä¸ª '[' ä¸æœ€åä¸€ä¸ª ']' ä¹‹é—´å­ä¸²
  const l = s.indexOf('[');
  const r = s.lastIndexOf(']');
  if(l>=0 && r>l){
    const mid = s.slice(l, r+1);
    try { const v = JSON.parse(mid); if(Array.isArray(v)) return v; } catch(_){}
  }

  // å®½æ¾ï¼šæŠŠå•å¼•å·è½¬åŒå¼•å·å†è¯•ï¼ˆé£é™©è¾ƒå°ï¼Œå…œåº•ç”¨ï¼‰
  try { const v = JSON.parse(s.replace(/'/g,'"')); if(Array.isArray(v)) return v; } catch(_){}

  return null;
}

// ---------- inputs ----------
const want = Number($items("Input")[0]?.json?.number_of_topics || 2);
const existingTitles = $items("Topics").map(i => i.json.title).filter(Boolean);
const existingNorm   = new Set(existingTitles.map(normalize));

const httpOut  = $items("Titles&Outlines Generation")[0]?.json;
const content  = httpOut?.choices?.[0]?.message?.content;
if(!content) throw new Error("Groq response missing choices[0].message.content");

let arr = extractJSONArray(content);
if(!arr) throw new Error("Cannot parse JSON array from LLM output.");

// ç»Ÿä¸€å­—æ®µå & æ¸…æ´—
arr = arr
  .map(o => {
    const title = String(o?.title || "").trim();
    const outlines = Array.isArray(o?.outlines) ? o.outlines :
                     Array.isArray(o?.outline)  ? o.outline  : [];
    return { title, outlines: outlines.map(x=>String(x||"").trim()).filter(Boolean) };
  })
  .filter(o => o.title); // è‡³å°‘è¦æœ‰æ ‡é¢˜

// ---------- ç»„è£…é€»è¾‘ï¼šä¸¥æ ¼ -> æ”¾å®½ -> æœ€ç»ˆè¡¥æ»¡ ----------
const picked = [];
const usedSlug = new Set();

// è¿‘ä¼¼å»é‡ï¼ˆä¸¥æ ¼ç‰ˆï¼‰
function passStrict(o){
  const tNorm = normalize(o.title);
  if (existingNorm.has(tNorm)) return false;
  if (existingTitles.some(t => softSim(t, o.title) >= 0.82)) return false;
  if (picked.some(p => softSim(p.title, o.title) >= 0.82)) return false;
  return true;
}
// æ”¾å®½å»é‡ï¼ˆæœ€åå…œåº•ï¼‰
function passLoose(o){
  if (picked.some(p => normalize(p.title) === normalize(o.title))) return false;
  return true;
}

// ç”Ÿæˆå”¯ä¸€ slug
function uniqSlug(baseTitle){
  let slug = slugify(baseTitle) || "topic";
  const base = slug;
  let k = 2;
  while(usedSlug.has(slug)) slug = `${base}-${k++}`;
  usedSlug.add(slug);
  return slug;
}

// ä¸€æ¬¡å…¥ç¯®çš„å·¥å…·
function pushItem(o){
  picked.push({
    title: o.title,
    outlines: padTo5(o.title, o.outlines),
    slug: uniqSlug(o.title),
  });
}

// Round 1: å…ˆæŒ‘ â‰¥5 æ¡ outline çš„ï¼Œä¸¥æ ¼å»é‡
for(const o of arr){
  if(picked.length >= want) break;
  if(o.outlines.length >= 5 && passStrict(o)) pushItem(o);
}

// Round 2: å†æŒ‘ â‰¥3 æ¡ï¼ˆè¡¥é½åˆ° 5ï¼‰ï¼Œä¸¥æ ¼å»é‡
if(picked.length < want){
  for(const o of arr){
    if(picked.length >= want) break;
    if(o.outlines.length >= 3 && passStrict(o)) pushItem(o);
  }
}

// Round 3: å†æŒ‘ â‰¥1 æ¡ï¼ˆè¡¥é½åˆ° 5ï¼‰ï¼Œä¸¥æ ¼å»é‡
if(picked.length < want){
  for(const o of arr){
    if(picked.length >= want) break;
    if(o.outlines.length >= 1 && passStrict(o)) pushItem(o);
  }
}

// Round 4: è¿˜ä¸å¤Ÿå°±æ”¾å®½ï¼ˆä»…é˜²å®Œå…¨é‡å¤æ ‡é¢˜ï¼‰ï¼Œè¡¥æ»¡ want
if(picked.length < want){
  for(const o of arr){
    if(picked.length >= want) break;
    if(passLoose(o)) pushItem(o);
  }
}

// æˆªæ–­ï¼Œåªè¦ want ä¸ª
const final = picked.slice(0, want);

// è¾“å‡ºä¸º n8n æœŸæœ›çš„ items ç»“æ„
return final.map(x => ({ json: { title: x.title, outlines: x.outlines } }));

// === 0) å¯é€‰ï¼šä½ çš„å¾ªç¯èŠ‚ç‚¹åå­—ï¼ˆå¦‚æœä¸åŒï¼Œæ”¹æˆç›¸åŒåå­—ï¼›å–ä¸åˆ°ä¹Ÿä¸ä¼šæŠ¥é”™ï¼‰
const LOOP_NODE = "Loop Over Items";

// === 1) å– Groq HTTP çš„ HTMLï¼ˆå½“å‰ Code è¾“å…¥ï¼‰
const html = items[0].json?.choices?.[0]?.message?.content || "";
if (!html || typeof html !== "string") {
  throw new Error("No HTML content from Groq.");
}

// === 2) ä» HTML æŠ½å– <h1> ä½œä¸ºæ ‡é¢˜ï¼ˆå»æ ‡ç­¾/å‹ç©ºç™½ï¼‰
const h1 = html.match(/<h1[^>]*>([\s\S]*?)<\/h1>/i);
const titleFromHtml = h1 ? h1[1].replace(/<[^>]+>/g, " ").replace(/\s+/g, " ").trim() : "";

// === 3) æŠ½å–é¦–æ®µä½œä¸º excerptï¼ˆ<article> å†…çš„ç¬¬ä¸€ä¸ª <p>ï¼‰
const p = html.match(/<article[\s\S]*?<p>([\s\S]*?)<\/p>/i);
const firstPara = p ? p[1].replace(/<[^>]+>/g, " ").replace(/\s+/g, " ").trim() : "";

// === 4) å›é€€ï¼šå¦‚æœæ²¡æŠ½åˆ° <h1>ï¼Œå†å°è¯•ä» Loop Over Items å– title/idï¼ˆä¸ä¸­æ–­ï¼‰
let fallbackTitle = "";
let fallbackId = null;
try {
  // Code èŠ‚ç‚¹é‡Œï¼ŒæŒ‰å®˜æ–¹æ–¹å¼ç”¨ï¼š$items("èŠ‚ç‚¹å", runIndex, $itemIndex)
  const cur = $items(LOOP_NODE, 0, $itemIndex)?.json;
  if (cur) {
    fallbackTitle = cur.title || "";
    fallbackId = cur.id ?? null;
  }
} catch (e) { /* å¿½ç•¥ */ }

// === 5) æœ€ç»ˆè¾“å‡º
const finalTitle = titleFromHtml || fallbackTitle || "Untitled";
const finalId = fallbackId ?? $json.id ?? null;

return [{
  json: {
    id: finalId,                          // ä¾¿äºåé¢å›å†™æ•°æ®åº“
    title: finalTitle,                    // ç»™ WP çš„ Title
    content_html: html.trim(),            // ç»™ WP çš„ Content
    excerpt: firstPara.slice(0, 300)      // ç»™ WP çš„ Excerpt
  }
}];

